        this._snackBar = _snackBar;
        // VARIABLES
        this.editMode = [
            {
                name: '',
                notes: ''
            },
            false
        ];
        this.dbEntries = [];
        this.pageSizeOptions = [5, 10, 25, 50, 100];
        this.displayedColumns = ['name', 'actions'];
    }
    ngOnInit() {
        this.loadEntries();
    }
