    editEntry(id, name, notes) {
    }
    async removeEntry(id, name) {
        let currentUrl = this._router.url;
        switch (currentUrl) {
            case '/categorias':
                let answer = new Promise(this._categoriesService.getRecipesByCategory(id));
                answer.then(res => this.result = res);
                if (this.result) {
                }
                // this._categoriesService.removeCategory(id).subscribe(
                //   res => {
                //     this.updateDBSource(res)
                //   },
                //   err => console.log(err)
                // );
                break;
            case '/medidas':
                this._measuresService.removeMeasure(id).subscribe(res => this.updateDBSource(res), err => console.log(err));
                break;
            case '/niveles':
                this._levelsservice.removeLevel(id).subscribe(res => this.updateDBSource(res), err => console.log(err));
                break;
            case '/equipos':
                this._equipmentsService.removeEquipment(id).subscribe(res => this.updateDBSource(res), err => console.log(err));
                break;
            case '/ingredientes':
                console.log('INGREDIENTES');
                break;
            default:
                throw new Error('ENTRADA NO VALIDA');
        }
    }
    loadEntries() {
        let currentUrl = this._router.url;
        switch (currentUrl) {
            case '/categorias':
                this._categoriesService.getCategoryList().subscribe(res => this.updateDBSource(res), err => console.log(err));
                break;
            case '/medidas':
                this._measuresService.getMeasures().subscribe(res => this.updateDBSource(res), err => console.log(err));
                break;
            case '/niveles':
                this._levelsservice.getLevels().subscribe(res => this.updateDBSource(res), err => console.log(err));
                break;
            case '/equipos':
                this._equipmentsService.getEquipments().subscribe(res => this.updateDBSource(res), err => console.log(err));
                break;
            case '/ingredientes':
                console.log('INGREDIENTES');
                break;
            default:
                throw new Error('ENTRADA NO VALIDA');
        }
    }
    updateDBSource(res) {
        console.log(res);
        this.dbEntries = res;
        this.ENTRIES_DATA = new MatTableDataSource(this.dbEntries);
    }
};
